import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import Chat from '../components/Chat';
import axios from 'axios';

const Home = () => {
  const navigate = useNavigate();
  const [activeMenuItem, setActiveMenuItem] = useState('Home');
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');

  // Usar useCallback con navigate como dependencia
  const fetchUsername = useCallback(async () => {
    try {
      // Configurar axios para enviar credenciales
      axios.defaults.withCredentials = true;
      
      const response = await axios.get('http://localhost:5000/api/user/username');
      setUsername(response.data.nombre);
      setError('');
    } catch (error) {
      console.error('Error al obtener el nombre de usuario:', error);
      
      if (error.response) {
        // El servidor respondió con un error
        const errorMessage = error.response.data.error || 'Error al obtener nombre de usuario';
        setError(errorMessage);
        
        // Si el error es de autenticación, redirigir al login
        if (error.response.status === 401) {
          navigate('/');
        }
      } else if (error.request) {
        // La solicitud se hizo pero no hubo respuesta
        setError('No se pudo conectar con el servidor');
      } else {
        // Algo sucedió al configurar la solicitud
        setError('Error en la solicitud');
      }
    }
  }, [navigate]); // Agregar navigate como dependencia

  useEffect(() => {
    fetchUsername();
  }, [fetchUsername]); 

  const handleLogout = async () => {
    try {
      // Llamar a un endpoint de logout en el backend
      await axios.post('http://localhost:5000/api/logout', {}, {
        withCredentials: true
      });
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
    }
    
    // Limpiar cualquier dato de sesión en el frontend
    navigate("/");
  };

  const handleMenuItemClick = (label) => {
    setActiveMenuItem(label);
    
    switch(label) {
      case 'Recommendations':
        navigate('/recommendations');
        break;
      case 'Home':
        navigate('/home');
        break;
      default:
        // Manejar cualquier otro caso inesperado
        console.warn(`Menú no reconocido: ${label}`);
    }
  };

  // Función para renderizar el ícono de usuario
  const renderUserIcon = () => {
    const firstLetter = username ? username.charAt(0).toUpperCase() : 'U';
    return (
      <div className="w-12 h-12 bg-gray-300 flex items-center justify-center">
        <span className="text-xl font-bold text-gray-700">{firstLetter}</span>
      </div>
    );
  };

  return (
    <div className="flex h-screen">
      <aside className="w-72 bg-[#1c212c] flex flex-col items-center pt-5 pb-2 space-y-7">
        {/* Contenedor de Perfil o Logo */}
        <div className="flex items-center space-x-4 mb-5">
          {renderUserIcon()}
          <div>
            <h2 className="text-white font-semibold">
              {username || 'Usuario'}
            </h2>
            {error && (
              <p className="text-red-500 text-sm">{error}</p>
            )}
            <p className="text-gray-400 text-sm">Admin</p>
          </div>
        </div>

        {/* Menú de Navegación */}
        <div className="w-full pr-3 flex flex-col gap-y-1 text-gray-500 text-sm">
          <div className="font-QuicksandMedium pl-4 text-gray-400/60 text-xs uppercase">Menú</div>

          {/* Ítem de Menú: Home */}
          <MenuItem 
            label="Home" 
            isActive={activeMenuItem === 'Home'}
            onClick={() => handleMenuItemClick('Home')}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 fill-current" viewBox="0 0 24 24">
              <path d="M12 3L2 12h3v8h14v-8h3L12 3zm0 2.933L19 12h-2v8h-2v-6h-6v6H5v-8H3l9-6.067z"/>
            </svg>
          </MenuItem>

          {/* Ítem de Menú: Recommendations */}
          <MenuItem 
            label="Recommendations" 
            isActive={activeMenuItem === 'Recommendations'}
            onClick={() => handleMenuItemClick('Recommendations')}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 fill-current" viewBox="0 0 24 24">
              <path d="M19 3H5c-1.103 0-2 .897-2 2v14c0 1.103.897 2 2 2h14c1.103 0 2-.897 2-2V5c0-1.103-.897-2-2-2zm0 2v14H5V5h14z"/>
              <path d="M7 12h4v2H7zm6 0h4v2h-4z"/>
            </svg>
          </MenuItem>

          {/* Ítem de Menú: Salir */}
          <MenuItem 
            label="Salir" 
            onClick={handleLogout}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 fill-current" viewBox="0 0 24 24">
              <path d="M16 13v-2H7V8l-5 4 5 4v-3z"/>
              <path d="M20 3h-9c-1.103 0-2 .897-2 2v4h2V5h9v14h-9v-4H9v4c0 1.103.897 2 2 2h9c1.103 0 2-.897 2-2V5c0-1.103-.897-2-2-2z"/>
            </svg>
          </MenuItem>
        </div>
      </aside>

      {/* Área Principal */}
      <div className="flex-1 flex">
        {/* Componente de Chat que ocupa todo el espacio restante */}
        <Chat className="flex-1" style={{ borderRadius: '0' }} />
      </div>
    </div>
  );
};

// Componente de MenuItem
const MenuItem = ({ label, children, isActive, onClick }) => {
  return (
    <div 
      className="w-full flex items-center gap-x-1.5 group select-none cursor-pointer" 
      onClick={onClick}
    >
      <div
        className={`w-1 h-8 bg-transparent transition-colors duration-200 relative overflow-hidden ${
          isActive ? "bg-green-600" : ""
        }`}
      >
        <div className="absolute top-0 left-0 w-full h-full translate-y-full group-hover:translate-y-0 bg-green-600 transition-all duration-300"></div>
      </div>
      <div className="group-hover:bg-white/10 w-full group-active:scale-95 self-stretch pl-2 flex items-center space-x-2 transition-all duration-200 dark:group-hover:text-white text-sm">
        <div className="group -hover:text-green-600">{children}</div>
        <span className="font-QuicksandMedium">{label}</span>
      </div>
    </div>
  );
};

export default Home;
