import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import Chat from '../components/Chat';
import axios from 'axios';

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5000',
  withCredentials: true,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  }
});

const Home = () => {
  const navigate = useNavigate();
  const [activeMenuItem, setActiveMenuItem] = useState('Home');
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  const fetchUsername = useCallback(async () => {
    try {
      const response = await axiosInstance.get('/api/user/profile');
      if (response.data.status === 200) {
        setUsername(response.data.nombre);
        setError('');
      } else {
        throw new Error(response.data.error || 'Error al obtener nombre de usuario');
      }
    } catch (error) {
      console.error('Error:', error);
      if (error.response?.status === 401) navigate('/');
      setError('No autorizado');
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    fetchUsername();
  }, [fetchUsername]);

  const handleLogout = async () => {
    try {
      await axiosInstance.post('/api/logout');
      localStorage.removeItem('token');
      sessionStorage.removeItem('user');
      navigate('/');
    } catch (error) {
      setError('No se pudo cerrar la sesión');
    }
  };

  if (loading) return <p>Cargando...</p>;

  return (
    <div className="flex h-screen">
      <aside className="w-72 bg-[#1c212c] flex flex-col items-center pt-5 pb-2 space-y-7">
        <div className="flex items-center space-x-4 mb-5">
          <div className="w-12 h-12 bg-gray-300 flex items-center justify-center rounded-full">
            <span className="text-xl font-bold text-gray-700">
              {username?.charAt(0)?.toUpperCase() || 'U'}
            </span>
          </div>
          <div>
            <h2 className="text-white font-semibold">{username || 'Usuario'}</h2>
            {error && <p className="text-red-500 text-sm">{error}</p>}
          </div>
        </div>
        <MenuItem label="Home" isActive={activeMenuItem === 'Home'} onClick={() => setActiveMenuItem('Home')}>
          <HomeIcon />
        </MenuItem>
        <MenuItem label="Recommendations" isActive={activeMenuItem === 'Recommendations'} onClick={() => setActiveMenuItem('Recommendations')}>
          <RecommendationIcon />
        </MenuItem>
        <MenuItem label="Salir" onClick={handleLogout}>
          <LogoutIcon />
        </MenuItem>
      </aside>
      <div className="flex-1 flex">
        <Chat className="flex-1" style={{ borderRadius: '0' }} />
      </div>
    </div>
  );
};

export default Home;
