import React, { useState, useEffect, useRef } from 'react';

const Chat = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Hola, ¿en qué puedo ayudarte hoy?",
      sender: 'ai'
    }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const messagesEndRef = useRef(null);

  // Función para enviar mensaje
  const handleSendMessage = async () => {
    if (inputMessage.trim() === '') return;

    // Añadir mensaje del usuario
    const userMessage = {
      id: messages.length + 1,
      text: inputMessage,
      sender: 'user'
    };

    setMessages(prevMessages => [...prevMessages, userMessage]);
    setInputMessage('');

    try {
      // Llamada al endpoint para obtener recomendación
      const response = await fetch('http://localhost:5000/recommend_by_nutrition', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include', // Importante para enviar cookies de sesión
        body: JSON.stringify({
          peso: 75,
          altura: 175,
          edad: 30,
          genero: 'hombre',
          nivel_actividad: 'moderado'
        })
      });

      if (response.ok) {
        const data = await response.json();
        
        // Formatear la respuesta para mostrar en el chat
        const aiResponse = {
          id: messages.length + 2,
          text: `Basado en tus datos nutricionales:\n
          Necesidades calóricas: ${data.necesidades.calorias} kcal\n
          Proteínas recomendadas: ${data.necesidades.proteinas}g\n
          Recetas recomendadas: ${data.recetas.map(r => r.nombre).join(', ')}`,
          sender: 'ai'
        };

        setMessages(prevMessages => [...prevMessages, aiResponse]);
      } else {
        throw new Error('Error en la solicitud');
      }
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        id: messages.length + 2,
        text: "Lo siento, hubo un error al procesar tu solicitud.",
        sender: 'ai'
      };
      setMessages(prevMessages => [...prevMessages, errorMessage]);
    }
  };

  // Scroll automático al final de los mensajes
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Manejar envío con Enter
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <div className="w-full h-full flex flex-col bg-white">
      {/* Encabezado del Chat */}
      <div className="bg-[#2c3442] text-white p-4 flex justify-between items-center">
        <h2 className="text-lg font-semibold">Asistente Nutricional</h2>
      </div>

      {/* Área de Mensajes */}
      <div className="flex-1 bg-white overflow-y-auto p-4 space-y-4">
        {messages.map((message) => (
          <div 
            key={message.id} 
            className={`flex ${message.sender === 'ai' ? 'items-start' : 'justify-end items-start'} space-x-2`}
          >
            {message.sender === 'ai' && (
              <div className="w-8 h-8 bg-gray-600 rounded-full flex items-center justify-center">
                <span className="text-white font-bold">AI</span>
              </div>
            )}
            <div 
              className={`
                p-2 rounded-lg max-w-[70%] 
                ${message.sender === 'ai' 
                  ? 'bg-gray-100 text-black' 
                  : 'bg-green-600 text-white'}
              `}
            >
              {message.text}
            </div>
            {message.sender === 'user' && (
              <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center">
                <span className="text-white font-bold">U</span>
              </div>
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Área de Entrada de Mensaje */}
      <div className="bg-white p-4 flex items-center space-x-2 border-t border-gray-300">
        <input 
          type="text" 
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Escribe un mensaje..." 
          className="flex-1 bg-gray-100 text-black p-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-600"
        />
        <button 
          onClick={handleSendMessage}
          className="bg-green-600 text-white p-2 rounded-lg hover:bg-green-700"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 24 24" fill="currentColor">
            <path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"/>
          </svg>
        </button>
      </div>
    </div>
  );
};

export default Chat;
