import React, { useState } from 'react';
import '../styles/Login.css'; // Asegúrate de que este archivo esté en la ruta correcta

const Login = () => {
  const [isLogin, setIsLogin] = useState(true); // Para cambiar entre login y registro

  const toggleForm = () => {
    setIsLogin(!isLogin); // Cambia entre Login y Registro
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isLogin) {
      // Lógica para iniciar sesión
      console.log('Iniciando sesión...');
    } else {
      // Lógica para registrar
      console.log('Registrando nuevo usuario...');
    }
  };

  return (
    <section className="user">
      <div className="user_options-container">
        <div className="user_options-text">
          {isLogin ? (
            <div className="user_options-unregistered">
              <h2 className="user_unregistered-title">Don't have an account?</h2>
              <p className="user_unregistered-text">
                Sign up to access exclusive features and stay connected!
              </p>
              <button
                className="user_unregistered-signup"
                onClick={toggleForm}
              >
                Sign up
              </button>
            </div>
          ) : (
            <div className="user_options-registered">
              <h2 className="user_registered-title">Already have an account?</h2>
              <p className="user_registered-text">
                Log in to continue and enjoy personalized content.
              </p>
              <button className="user_registered-login" onClick={toggleForm}>
                Login
              </button>
            </div>
          )}
        </div>

        <div className="user_options-forms">
          <div className="user_forms">
            <h2 className="forms_title">{isLogin ? 'Login' : 'Sign Up'}</h2>
            <form className="forms_form" onSubmit={handleSubmit}>
              <fieldset className="forms_fieldset">
                <div className="forms_field">
                  <input
                    type="email"
                    placeholder="Email"
                    className="forms_field-input"
                    required
                    autoFocus
                  />
                </div>
                <div className="forms_field">
                  <input
                    type="password"
                    placeholder="Password"
                    className="forms_field-input"
                    required
                  />
                </div>
                {!isLogin && (
                  <div className="forms_field">
                    <input
                      type="password"
                      placeholder="Confirm Password"
                      className="forms_field-input"
                      required
                    />
                  </div>
                )}
              </fieldset>
              <div className="forms_buttons">
                <input
                  type="submit"
                  value={isLogin ? 'Log In' : 'Sign Up'}
                  className="forms_buttons-action"
                />
              </div>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
