import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Chat from '../components/Chat'; // Asegúrate de que la ruta de importación sea correcta
import axios from 'axios'; // Asegúrate de instalar axios: npm install axios

const Home = () => {
  const navigate = useNavigate();
  const [activeMenuItem, setActiveMenuItem] = useState('Home');
  const [username, setUsername] = useState(''); // Estado para el nombre del usuario

  // Función para obtener el nombre del usuario
  const fetchUsername = async () => {
    try {
      const response = await axios.get('/api/user/username', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}` // Si usas token para autenticación
        }
      });
      setUsername(response.data.nombre); // Asume que el endpoint devuelve un objeto con el nombre
    } catch (error) {
      console.error('Error al obtener el nombre de usuario:', error);
      setUsername('Usuario'); // Valor por defecto en caso de error
    }
  };

  useEffect(() => {
    fetchUsername(); // Llama a la función al montar el componente
  }, []);

  const handleLogout = () => {
    // Aquí podrías agregar lógica adicional de logout si es necesario
    navigate("/");
  };

  const handleMenuItemClick = (label) => {
    setActiveMenuItem(label);
    
    // Navegación condicional
    switch(label) {
      case 'Recommendations':
        navigate('/recommendations');
        break;
      case 'Home':
        navigate('/home');
        break;
      // Puedes agregar más casos según tus rutas
    }
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar / Menu Lateral */}
      <aside className="w-72 bg-[#1c212c] flex flex-col items-center pt-5 pb-2 space-y-7">
        {/* Contenedor de Perfil o Logo */}
        <div className="flex items-center space-x-4 mb-5">
          <div className="w-12 h-12 bg-gray-300 flex items-center justify-center">
            <span className="text-xl font-bold text-gray-700">{username.charAt(0).toUpperCase() || 'U'}</span>
          </div>
          <div>
            <h2 className="text-white font-semibold">{username || 'Usuario'}</h2>
            <p className="text-gray-400 text-sm">Admin</p>
          </div>
        </div>

        {/* Menú de Navegación */}
        <div className="w-full pr-3 flex flex-col gap-y-1 text-gray-500 text-sm">
          <div className="font-QuicksandMedium pl-4 text-gray-400/60 text-xs uppercase">Menú</div>

          {/* Ítem de Menú: Home */}
          <MenuItem 
            label="Home" 
            isActive={activeMenuItem === 'Home'}
            onClick={() => handleMenuItemClick('Home')}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 fill-current" viewBox="0 0 24 24">
              <path d="M5 22h14a2 2 0 0 0 2-2v-9a1 1 0 0 0-.38-.79l-7-6a1 1 0 0 0-1.24 0l-7 6A1 1 0 0 0 3 11v9a2 2 0 0 0 2 2zM5 11.47 12 5.53l7 5.94V20H5z"></path>
            </svg>
          </MenuItem>

          {/* Ítem de Menú: Recommendations */}
          <MenuItem 
            label="Recommendations" 
            isActive={activeMenuItem === 'Recommendations'}
            onClick={() => handleMenuItemClick('Recommendations')}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w ```javascript
            .5 fill-current" viewBox="0 0 24 24">
              <path d="M19 3H5c-1.103 0-2 .897-2 2v14c0 1.103.897 2 2 2h14c1.103 0 2-.897 2-2V5c0-1.103-.897-2-2-2zm0 2v14H5V5h14z"></path>
              <path d="M7 12h4v2H7zm6 0h4v2h-4z"></path>
            </svg>
          </MenuItem>

          {/* Ítem de Menú: Salir */}
          <MenuItem 
            label="Salir" 
            onClick={handleLogout}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 fill-current" viewBox="0 0 24 24">
              <path d="M16 13v-2H7V8l-5 4 5 4v-3z"></path>
              <path d="M20 3h-9c-1.103  0-2 .897-2 2v4h2V5h9v14h-9v-4H9v4c0 1.103.897 2 2 2h9c1.103 0 2-.897 2-2V5c0-1.103-.897-2-2-2z"></path>
            </svg>
          </MenuItem>
        </div>
      </aside>

      {/* Área Principal */}
      <div className="flex-1 flex">
        {/* Componente de Chat que ocupa todo el espacio restante */}
        <Chat className="flex-1" style={{ borderRadius: '0' }} />
      </div>
    </div>
  );
};

// Componente de MenuItem
const MenuItem = ({ label, children, isActive, onClick }) => {
  return (
    <div 
      className="w-full flex items-center gap-x-1.5 group select-none cursor-pointer" 
      onClick={onClick}
    >
      <div
        className={`w-1 h-8 bg-transparent transition-colors duration-200 relative overflow-hidden ${
          isActive ? "bg-green-600" : ""
        }`}
      >
        <div className="absolute top-0 left-0 w-full h-full translate-y-full group-hover:translate-y-0 bg-green-600 transition-all duration-300"></div>
      </div>
      <div className="group-hover:bg-white/10 w-full group-active:scale-95 self-stretch pl-2 flex items-center space-x-2 transition-all duration-200 dark:group-hover:text-white text-sm">
        <div className="group-hover:text-green-600">{children}</div>
        <span className="font-QuicksandMedium">{label}</span>
      </div>
    </div>
  );
};

export default Home;
