import React from "react";
import { useHistory } from "react-router-dom"; // Importar useHistory

const Home = () => {
  const history = useHistory(); // Inicializar useHistory

  const handleLogout = () => {
    // Aquí puedes realizar cualquier acción necesaria para cerrar sesión
    // Luego redirigir al login
    history.push('/login'); // Redirigir a la página de login
  };

  return (
    <aside className="w-72 bg-[#1c212c] min-h-full h-screen flex flex-col items-center pt-5 pb-2 space-y-7">
      {/* Menu Items */}
      <div className="w-full pr-3 flex flex-col gap-y-1 text-gray-500 text-sm">
        <div className="font-QuicksandMedium pl-4 text-gray-400/60 text-xs text-[11px] uppercase">Menu</div>

        <MenuItem label="Home" isActive={true}>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 fill-current" viewBox="0 0 24 24" fill="currentColor">
            <path d="M5 22h14a2 2 0 0 0 2-2v-9a1 1 0 0 0-.38-.79l-7-6a1 1 0 0 0-1.24 0l-7 6A1 1 0 0 0 3 11v9a2 2 0 0 0 2 2zM5 11.47 12 5.53l7 5.94V20H5z"></path>
          </svg>
        </MenuItem>

        {/* Otros elementos del menú... */}

        <MenuItem label="Salir" onClick={handleLogout}> {/* Añadir el botón de salir */}
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 fill-current" viewBox="0 0 24 24" fill="currentColor">
            <path d="M10 17l5-5-5-5v10zm9-7h-3V4h-2v6H5v2h9v6h2v-6h3v-2z"></path>
          </svg>
        </MenuItem>
      </div>

      {/* Profile Section */}
      <div className="w-full pr-3 flex flex-col gap-y-1 text-gray-500 text-sm">
        <div className="font-QuicksandMedium pl-4 text-gray-400/60 text-xs text-[11px] uppercase">Profile</div>
        <MenuItem label="Edit Profile">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 fill-current" viewBox="0 0 24 24" fill="currentColor">
            <path d="M18.99 2L2 18.99V22h3.01L22 5.01 18.99 2zM4 20v-1.99l14-14L18.99 6 5 19H4zm14.8-10.8l1.4 1.4-1.79 1.79-1.4-1.4L18.8 9.2z"></path>
          </svg>
        </MenuItem>

        <MenuItem label="Settings">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 fill-current" viewBox="0 0 24 24" fill="currentColor">
            <path d="M12 15.5a3.5 3.5 0 1 0 0-7 3.5 3.5 0 0 0 0 7zm0-10c3.54 0 6.48 2.94 6.48 6.48S15.54 18.5 12 18.5s-6.48-2.94-6.48-6.48S8.46 5.5 12 5 .5z"></path>
          </svg>
        </MenuItem>
      </div>
    </aside>
  );
};

const MenuItem = ({ label, children, isActive, onClick }) => {
  return (
    <div className="w-full flex items-center gap-x-1.5 group select-none" onClick={onClick}>
      <div
        className={`w-1 rounded-xl h-8 bg-transparent transition-colors duration-200 relative overflow-hidden ${
          isActive ? "bg-green-600" : ""
        }`}
      >
        <div className="absolute top-0 left-0 w-full h-full translate-y-full group-hover:translate-y-0 bg-green-600 transition-all duration-300"></div>
      </div>
      <div className="group-hover:bg-white/10 w-full group-active:scale-95 self-stretch pl-2 rounded flex items-center space-x-2 transition-all duration-200 dark:group-hover:text-white text-sm">
        <div className="group-hover:text-green-600">{children}</div>
        <span className="font-QuicksandMedium">{label}</span>
      </div>
    </div>
  );
};

export default Home;
