import React from "react";
import { Link } from "react-router-dom";

const Home = () => {
  return (
    <div style={{ textAlign: "center", padding: "2rem" }}>
      <h1>Bienvenido a HealthyRecipes</h1>
      <p>
        Explora recetas saludables diseñadas para estudiantes de software, optimizadas para
        tu estilo de vida y necesidades académicas.
      </p>

      <div style={{ marginTop: "2rem" }}>
        <Link to="/recommendations" style={{ textDecoration: "none" }}>
          <button style={styles.button}>Obtener Recomendaciones</button>
        </Link>
        <Link to="/meal-planner" style={{ textDecoration: "none", marginLeft: "1rem" }}>
          <button style={styles.button}>Planificar Comidas</button>
        </Link>
      </div>

      <div style={{ marginTop: "2rem" }}>
        <img
          src="/assets/healthy-food.jpg" // Asegúrate de tener esta imagen en la carpeta `public/assets`
          alt="Healthy food"
          style={styles.image}
        />
      </div>
    </div>
  );
};

const styles = {
  button: {
    padding: "0.75rem 1.5rem",
    fontSize: "1rem",
    color: "white",
    backgroundColor: "#28a745",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  image: {
    width: "80%",
    maxWidth: "500px",
    borderRadius: "10px",
    marginTop: "1rem",
  },
};

export default Home;
