import React, { useState } from 'react';
import axios from 'axios';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/login', {
        email,
        password,
      });

      if (response.status === 200) {
        alert('Login exitoso');
        console.log('Usuario:', response.data.nombre);
        // Redirigir o hacer otra acci칩n
      }
    } catch (error) {
      alert('Error en el login: ' + error.response.data.message);
    }
  };

  return (
    <div className="font-sans">
      <div className="relative min-h-screen flex flex-col sm:justify-center items-center bg-gray-100">
        <div className="relative sm:max-w-sm w-full">
          <div className="relative w-full rounded-3xl px-6 py-4 bg-gray-100 shadow-md">
            <label className="block mt-3 text-sm text-gray-700 text-center font-semibold">
              Login
            </label>
            <form className="mt-10" onSubmit={handleSubmit}>
              <div>
                <input
                  type="email"
                  placeholder="Correo electr칩nico"
                  className="mt-1 block w-full bg-gray-100 h-11 rounded-xl"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              <div className="mt-7">
                <input
                  type="password"
                  placeholder="Contrase침a"
                  className="mt-1 block w-full bg-gray-100 h-11 rounded-xl"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>
              <div className="mt-7">
                <button type="submit" className="bg-blue-500 w-full py-3 rounded-xl text-white">
                  Login
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
