// C:\Users\Jhon\Documents\8vo\Aplicaciones\proyecto\programa\project-root\frontend\src\components\Chat.js
import React, { useState } from 'react';

const Chat = () => {
  const [messages, setMessages] = useState([
    { id: 1, text: "Hola, ¿cómo puedo ayudarte?", sender: "bot" },
  ]);
  const [inputMessage, setInputMessage] = useState('');

  const handleSendMessage = () => {
    if (inputMessage.trim() === '') return;

    const newMessage = {
      id: messages.length + 1,
      text: inputMessage,
      sender: 'user'
    };

    setMessages([...messages, newMessage]);
    setInputMessage('');

    // Simulación de respuesta del bot
    setTimeout(() => {
      const botResponse = {
        id: messages.length + 2,
        text: `Entendido: ${inputMessage}`,
        sender: 'bot'
      };
      setMessages(prevMessages => [...prevMessages, botResponse]);
    }, 1000);
  };

  return (
    <div className="flex-1 bg-white shadow-lg rounded-lg overflow-hidden">
      {/* Encabezado del Chat */}
      <div className="bg-[#1c212c] text-white p-4 flex justify-between items-center">
        <h2 className="text-lg font-semibold">Mensajes</h2>
        <div className="flex items-center space-x-2">
          <span className="text-sm text-gray-300">En línea</span>
          <div className="w-2 h-2 bg-green-500 rounded-full"></div>
        </div>
      </div>

      {/* Área de Mensajes */}
      <div className="p-4 h-[calc(100vh-250px)] overflow-y-auto">
        {messages.map((message) => (
          <div 
            key={message.id} 
            className={`mb-4 flex ${
              message.sender === 'user' ? 'justify-end' : 'justify-start'
            }`}
          >
            <div 
              className={`
                max-w-[70%] p-3 rounded-lg 
                ${message.sender === 'user' 
                  ? 'bg-green-500 text-white' 
                  : 'bg-gray-200 text-black'}
              `}
            >
              {message.text}
            </div>
          </div>
        ))}
      </div>

      {/* Área de Entrada de Mensajes */}
      <div className="p-4 border-t bg-gray-100 flex items-center">
        <input 
          type="text" 
          placeholder="Escribe un mensaje..." 
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
          className="flex-1 p-2 border rounded-l-lg focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        <button 
          onClick={handleSendMessage}
          className="bg-green-500 text-white p-2 rounded-r-lg hover:bg-green-600 transition-colors"
        >
          Enviar
        </button>
      </div>
    </div>
  );
};

export default Chat;
